var OwlCell = React.createClass({
	displayName: 'OwlCell',
	propTypes: {
		column: React.PropTypes.object.isRequired,
		row: React.PropTypes.object,
		open: React.PropTypes.bool,
		editable: React.PropTypes.bool
	},
	getDefaultProps: function () {
		return {
			open: false,
			editable: true
		};
	},
	render: function () {
		var props = this.props;
		var content = React.createElement("span", {className: "owl-cell-value-label"}, props.row[props.column.field]);

		props.editable = props.column.editable || true;

		if (props.open === true) {
			content = React.createElement(OwlInput, {
						column: props.column, 
						value: props.row[props.column.field], 
						row: props.row, 
						tableDidChange: props.tableDidChange}
					);
		}

		return (
			React.createElement("td", {'data-field': props.column.field}, 
				props.editable === true ? content : (props.row[props.column.field] || '---')
			)
		);
	},
	componentDidUpdate: function () {
		if (this.props.focusedCell !== false) {
			this.props.focusedCell.find('input').focus();
		}
	}
});

var OwlRow = React.createClass({
	displayName: 'OwlRow',
	propTypes: {
		data: React.PropTypes.object.isRequired,
		columns: React.PropTypes.array.isRequired,
		open: React.PropTypes.bool.isRequired
	},
	getInitialState: function () {
		return {
			open: false,
			focusedCell: false
		};
	},
	clickHandler: function (event) {
		var cell = $(event.target);
		// need to focus the input that was clicked
		if (event.target.nodeName !== 'TD') {
			cell = cell.closest('td');
		}

		this.setState({
			open: true,
			focusedCell: cell
		});
	},
	render: function () {
		var props = this.props;
		var state = this.state;

		var handler = this.clickHandler;

		var cells = props.columns.map(function (column, index) {
			return (
				React.createElement(OwlCell, {column: column, row: props.data, focusedCell: state.focusedCell, open: props.open || state.open, key: index, tableDidChange: props.tableDidChange})
			);
		});

		return(
			React.createElement("tr", {className: "owl-row", key: props.key, onClick: this.clickHandler}, 
				cells
			)
		);
	}
});

var OwlTableReact = React.createClass({
	displayName: 'OwlTable',
	propTypes: {
		data: React.PropTypes.array.isRequired,
		columns: React.PropTypes.array.isRequired,
		tacky: React.PropTypes.object
	},
	getInitialState: function () {
		return {
			changedData: {},
			openRows: {}
		};
	},
	componentDidMount: function () {

	},
	componentDidUpdate: function () {
		if (this.props.tacky) {
			$('.tacky').tacky();
		}
	},
	render: function () {
		var self = this;

		var props = self.props;
		var tackyTop = false;
		var tackyLeft = false;

		if (props.tacky) {
			if (props.tacky.top) {
				tackyTop = true;
			}

			if (props.tacky.left) {
				tackyLeft = props.tacky.left;
			}
		}

		var headers = props.columns.map(function (column, index) {
			return (
				React.createElement("th", {className: tackyTop ? 'tacky-top' : '', key: index, 'data-field': column.field}, 
					column.title || 'None'
				)
			);
		});


		var rows = props.data.map(function (datum, index) {
			return (
				React.createElement(OwlRow, {data: datum, columns: props.columns, key: index, open: self.state.openRows[index] || false, tableDidChange: self.tableDidChange})
			);
		});

		self.keyup = function (event) {
			var td = $(event.target).parent();
			var handled = false;

			switch (event.which) {
				case 9:
					if (event.shiftKey !== true) {
						td.next().children().focus();
					} else {
						td.prev().children().focus();
					}
					handled = true;
					break;
				default:
					break;
			}

			if (handled === true) {
				event.stopPropagation();
			}
		};

		return (
			React.createElement("table", {onKeyUp: self.keyup, className: "owl-table"}, 
				React.createElement("thead", null, 
					headers
				), 
				React.createElement("tbody", null, 
					rows
				)
			)
		);
	}
});

